con:config.script:
def cases = testRunner.testCase.getTestStepByName( "Test Cases" );
def parameters = testRunner.testCase.getTestStepByName( "Test Parameters" );

String  sCounter = parameters.getPropertyValue("testNumber");
Integer	counter = Integer.valueOf(sCounter);
if (counter < 0)
	counter = 1;
else
	counter++;

if (counter < 10)
	sCounter = "00" + counter;
else if (counter < 100)
	sCounter = "0" + counter;
else
	sCounter = "" + counter;

String sTestProperty = "testSpec" + sCounter;
String  sTestSpec = cases.getPropertyValue(sTestProperty);

if (sTestSpec == null)
{
	/* Reset counter */
	log.info ("Finished Tests");
	parameters.setPropertyValue("testNumber", "0");	
	testRunner.gotoStepByName("End");
}
else
{
	String [] fields = sTestSpec.split("%");
	if (fields.length != 11)
	{
		log.error ("Wrong number of parameters in test #" + counter + ":" + sTestSpec);
		parameters.setPropertyValue("testNumber", (-counter)+"");	
		//testRunner.gotoStepByName("End");
	}
	else 
	{
		log.info ("===============================================");
		log.info ("Test #" + sCounter + ":" + sTestSpec);
		// Increment test counter
		parameters.setPropertyValue("testNumber", counter+"");
		parameters.setPropertyValue("CD_UT",   fields[0]);	
		parameters.setPropertyValue("myBoolean",   fields[1]);	
		parameters.setPropertyValue("myDate", fields[2]);	
		parameters.setPropertyValue("myDateTime",   fields[3]);	
		parameters.setPropertyValue("myInteger",   fields[4]);
		parameters.setPropertyValue("myPerformer",   fields[5]);
		parameters.setPropertyValue("myText",   fields[6]);
		parameters.setPropertyValue("myTime",   fields[7]);	
		parameters.setPropertyValue("myDecimal",   fields[8]);
		parameters.setPropertyValue("ACTIVITY_TYPE",   fields[9]);
		parameters.setPropertyValue("MAIL_TO",   fields[10]);

		//propparameters.setPropertyValue("pActual",""); // Clear results field
		//propparameters.setPropertyValue("pResult",""); // Clear results field		
	}
}

