package com.tibco.customer.api.test.iprocess.amxbpm.framework.test.validations;

import java.util.List;

import org.eclipse.core.runtime.IStatus;

import com.tibco.customer.api.test.iprocess.amxbpm.AbstractIProcessConversionValidationTest;
import com.tibco.xpd.core.test.util.TestResourceInfo;
import com.tibco.xpd.iprocess.amxbpm.imports.validations.ImportValidationError;

/**
 * IPS to BPM validator Test that chekcs if expected number of warning messages
 * are generated by the validator when the Studio iProcess xpdl to convert has
 * broken re-usable sub process refernces.
 * 
 * @author kthombar
 * @since 23-Jun-2014
 */
public class IpsBpm_01_SubProcReferencesBrokenTest extends
        AbstractIProcessConversionValidationTest {

    /**
     * @see com.tibco.customer.api.test.iprocess.amxbpm.AbstractIProcessConversionValidationTest#doTestImportValidations(java.util.List)
     * 
     * @param errors
     */
    @Override
    protected void doTestImportValidations(List<ImportValidationError> errors) {

        /*
         * We expect 3 warning status to be generated
         */
        assertEquals("Unexpected number of warnings generated by the validator", //$NON-NLS-1$
                3,
                errors.size());

        for (ImportValidationError importValidationError : errors) {
            /*
             * There should be no other messgae other than warning messages.
             */
            if (IStatus.WARNING != importValidationError.getSeverity()) {
                fail("The validator should only generate broken refernces warning status messages."); //$NON-NLS-1$
            }
        }

    }

    /**
     * @see com.tibco.customer.api.test.iprocess.amxbpm.AbstractIProcessConversionValidationTest#getConversionType()
     * 
     * @return
     */
    @Override
    protected CONVERSION_TYPE getConversionType() {
        return CONVERSION_TYPE.IPS_TO_BPM_CONVERT;
    }

    /**
     * @see com.tibco.customer.api.test.iprocess.amxbpm.AbstractBaseIpmBpmTest#getImportResourcesInfo()
     * 
     * @return
     */
    @Override
    public TestResourceInfo[] getImportResourcesInfo() {
        TestResourceInfo[] testResources =
                new TestResourceInfo[] { new TestResourceInfo(
                        "resources/FrameworkValidationTests", "IpsBpm_01_SubProcReferencesBrokenTest/Process Packages{processes}/BrokenReferences.xpdl"), //$NON-NLS-1$ //$NON-NLS-2$
                };
        return testResources;
    }

    /**
     * @see com.tibco.customer.api.test.iprocess.amxbpm.AbstractBaseIpmBpmTest#getOtherResourcesInfo()
     * 
     * @return
     */
    @Override
    public TestResourceInfo[] getOtherResourcesInfo() {
        // Do nothing
        return null;
    }

    /**
     * @see com.tibco.customer.api.test.iprocess.amxbpm.AbstractBaseIpmBpmTest#getMainImportProjectName()
     * 
     * @return
     */
    @Override
    protected String getMainImportProjectName() {

        return "IpsBpm_01_SubProcReferencesBrokenTest"; //$NON-NLS-1$
    }

}
