/**
 * N2WebServiceXPathValidMappingsTest
 * <p>
 * N2WebServiceXPathValidMappingsTest - Test selected validations are correctly
 * raised.
 * </p>
 * <p>
 * Generated by: createBaseTest.javajet
 * </p>
 * 
 * Test that valid WebService XPath mappings pass validation for BPMN/N2
 * 
 * @author
 * @since
 */
public class N2_11_N2WebServiceXPathValidMappingsTest extends
        AbstractN2BaseValidationTest {

    private static String[] ISSUE_ID_EXTRACTS =
            new String[] { "abstractMappingRule\\..*" }; //$NON-NLS-1$

    public N2_11_N2WebServiceXPathValidMappingsTest() {
        super(true);
    }

    /**
     * Override to check against specific validations being raised.
     */
    @Override
    protected void doTestValidations() throws Exception {
        String failures = ""; //$NON-NLS-1$
        TestResourceInfo[] testResources = getTestResources();

        for (TestResourceInfo testResourceInfo : testResources) {
            IFile testFile = testResourceInfo.getTestFile();
            if (testFile == null || !testFile.exists()) {
                fail("Unable to find resource '" + testResourceInfo.getTestFilePath() //$NON-NLS-1$
                        + "' for  test problem markers"); //$NON-NLS-1$
            }

            List<ValidationsTestProblemMarkerInfo> problemMarkers =
                    getProblemMarkers(testFile);
            for (ValidationsTestProblemMarkerInfo markerInfo : problemMarkers) {
                String problemId = markerInfo.getProblemId();

                for (String issueIdPattern : ISSUE_ID_EXTRACTS) {
                    if (problemId.matches(issueIdPattern)) {
                        failures +=
                                String.format("Expected no occurrences of issue '%s'\n  (matches issue id pattern '%s'; found in resource: '%s').\n", //$NON-NLS-1$
                                        markerInfo.getProblemText(),
                                        issueIdPattern,
                                        testResourceInfo.getTestFilePath());
                    }
                }
            }
        }

        if (failures.length() > 0) {
            fail(failures);
        }
        return;
    }

    /**
     * N2WebServiceXPathValidMappingsTest
     * 
     * @throws Exception
     */
    public void testN2WebServiceXPathValidMappingsTest() throws Exception {
        doTestValidations();
        return;
    }

    @Override
    protected ValidationsTestProblemMarkerInfo[] getValidationProblemMarkerInfos() {
        ValidationsTestProblemMarkerInfo[] markerInfos =
                new ValidationsTestProblemMarkerInfo[] {

                };
        return markerInfos;
    }

    @Override
    protected String getTestName() {
        return "N2WebServiceXPathValidMappingsTest"; //$NON-NLS-1$
    }

    @Override
    protected String getTestPlugInId() {
        return "com.tibco.xpd.n2.mappings.validation.test"; //$NON-NLS-1$
    }

    @Override
    protected TestResourceInfo[] getTestResources() {
        TestResourceInfo[] testResources =
                new TestResourceInfo[] {
                        new TestResourceInfo(
                                "resources/N2 WebService XPath Valid Mappings", "TestWebServiceXPathValidMappings/Service Descriptors{wsdl}/NewWSDLFile.wsdl"), //$NON-NLS-1$ //$NON-NLS-2$
                        new TestResourceInfo(
                                "resources/N2 WebService XPath Valid Mappings", "TestWebServiceXPathValidMappings/Process Packages{processes}/TestWebServiceXPathValidMappings.xpdl"), //$NON-NLS-1$ //$NON-NLS-2$
                };

        return testResources;
    }

}
